import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState } from 'react';

/* incrementar = () =>{
  console.log("click")
} */

const App = (props) => {

  const [contador, setContador] = useState(0)

  console.log("render")

const handleClickAdd = () =>{
  setContador(contador + 1)
}
const handleClickRestart = () =>{
  setContador(0)
}
const handleClickSubtract = () =>{
  setContador(contador - 1)
}

const isEven = () =>{
  let even = contador % 2
  if(even === 0){
    return "el contador es par"
  }else{
    return "el contador es impar"
  }
}

  return (
    <div>
    <h1> {contador} </h1>
    <p>El valor del contador es:</p>
    <small>{isEven()}</small>
    <button onClick={handleClickAdd}>incrementar</button>
    <button onClick={handleClickRestart}>resetear</button>
    <button onClick={handleClickSubtract}>disminuir</button>
    </div>
  )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
 <App/>
);
