import React from 'react';
import ReactDOM from 'react-dom/client';

const App = (props) => {
  return <h1> {props.contadorInicial} </h1>
}

let contador = 0

const refresh = () =>{
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
 <App contadorInicial={contador}/>
);
}
setInterval(() => {
  contador++
  refresh()
}, 10000);
