import React from 'react';
import ReactDOM from 'react-dom/client';

const App = (props) => {
  return (
  <div>
  <h1> {props.contadorInicial} </h1>
  </div>
  )
}

let contador = 0

const refresh = () =>{
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
 <App contadorInicial={contador}/>
);
}

setInterval(() => {
  contador++
  refresh()
}, 1000);
